import React, {Component} from 'react';
import axios from 'axios';

export default class CreateUser extends Component {

    constructor(props) {
        super(props);

        this.state = {
            username: '',
            password: '',
            type: '',
            errors:{}
        }
        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        this.onChangeType = this.onChangeType.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }

    onChangeType(event) {
        console.log(event.target.value);
        this.setState({ type: event.target.value });
    }

    onChangeUsername(event) {
        this.setState({ username: event.target.value });
        this.state.errors["name"]="cant be empty";

    }
    onChangePassword(event) {
        this.setState({ password: event.target.value });
    }

    onSubmit(e) {
        e.preventDefault();

        const newUser = {
            // _id : this.state.username,
            username: this.state.username,
            password: this.state.password

        }
        if (this.state.type==="Vendor") 
        {
            axios.post('http://localhost:4000/vendoradd', newUser)
            .then(res => console.log(res.data));

        }
        else if(this.state.type==="Customer")
        {
            axios.post('http://localhost:4000/customeradd', newUser)
            .then(res => console.log(res.data));
        }     
        this.setState({
            username: '',
            password: ''
        });
    }

    render() {
        return (
            <div>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Username: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.username}
                               onChange={this.onChangeUsername}
                               />
                                <div style={{color:"red"}} className="error">{this.state.errors["name"]}</div>
                    </div>
                    <div className="form-group">
                        <label>Password: </label>
                        <input type="password" 
                               className="form-control" 
                               value={this.state.password}
                               onChange={this.onChangePassword}
                               />  
                    </div>
                    <div className="form-group">
                             <label>Vendor</label>
                            <input type="radio" name="type"  value="Vendor" onClick={this.onChangeType}/> 
                             <label>Customer</label>
                            <input type="radio" name="type" value="Customer" onClick={this.onChangeType}/>  
                    </div>
                    <div className="form-group">
                        <input type="submit" value="Create User" className="btn btn-primary"/>
                    </div>
                </form>
            </div>
        )
    }
}
