import React, {Component} from 'react';
import "bootstrap/dist/css/bootstrap.min.css"
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

import axios from 'axios';

export default class CustomerViewOrder extends Component {

    constructor(props) {
        super(props);
        this.state = {
                        users :[],
                        id: this.props.match.params.id,
                        username :this.props.match.params.id,
                        value : '',
                        orderarr : {},
                        freevar : 0,
                        searcherror : '',
                        tosearch : ''
                     }
        // this.state.orderarr['m']="2";
        this.orderfun = this.orderfun.bind(this);
        this.onChangeorder = this.onChangeorder.bind(this);
        this.sortbyprice = this.sortbyprice.bind(this);
        this.onChangeSearch = this.onChangeSearch.bind(this);
        this.sortbyquantity = this.sortbyquantity.bind(this);

    }

    onChangeSearch =  event =>
    {
      this.state.tosearch=event.target.value;
      console.log(3412)
        axios.get('http://localhost:4000/product/search'+"/waiting/"+this.state.tosearch)
             .then(response => {
                 this.setState({users: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })


    }

    sortbyprice = arg=> event =>
    {
        axios.get('http://localhost:4000/product/sort/bundlePrice'+"/waiting/"+arg)
             .then(response => {
                 this.setState({users: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })


    }

    sortbyquantity = arg=> event =>
    {
        axios.get('http://localhost:4000/product/sort/leftQuantity'+"/waiting/"+arg)
             .then(response => {
                 this.setState({users: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })


    }
    onChangeorder = arg => event =>
    {
        this.state.orderarr[arg["currentUser"]["_id"]] = event.target.value;  
        this.setState({ freevar : 1});
        console.log(this.state.orderarr);
        axios.get('http://localhost:4000/product/')
             .then(response => {
                 this.setState({users: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
    }

    orderfun = arg => event =>
    {
         console.log(arg["currentUser"]["_id"]);
         if (parseInt(this.state.orderarr[arg["currentUser"]["_id"]])<=parseInt(arg["currentUser"]["leftQuantity"]))
         {
          var proddet;
          if (parseInt(arg["currentUser"]["leftQuantity"])-parseInt(this.state.orderarr[arg["currentUser"]["_id"]])>0)
          {
              proddet = {
                  id :   arg["currentUser"]["_id"],
                  newleftQuantity : parseInt(arg["currentUser"]["leftQuantity"])-parseInt(this.state.orderarr[arg["currentUser"]["_id"]]),
                  status : "waiting"
             }
          }
          else
          {
                proddet = {
                  id :   arg["currentUser"]["_id"],
                  newleftQuantity : parseInt(arg["currentUser"]["leftQuantity"])-parseInt(this.state.orderarr[arg["currentUser"]["_id"]]),
                  status : "dispatch"
          } 
              }
              const orderdet = {
                  ProductId :   arg["currentUser"]["_id"],
                  username : this.username,
                  quantity : parseInt(this.state.orderarr[arg["currentUser"]["_id"]])
              }
             axios.post('http://localhost:4000/product/update', proddet)
            .then(res => console.log(res.data));
            axios.post('http://localhost:4000/orders/add', orderdet)
            .then(res => console.log(res.data));
         }
         else
         {

         }

    }

    componentDidMount() {
        axios.get('http://localhost:4000/product/')
             .then(response => {
                 this.setState({users: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
    }

    render() {
        return (
                  <div className="container">
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <Link to="/" className="navbar-brand"> App </Link>

          <div className="collapse navbar-collapse">
            <ul className="navbar-nav mr-auto">
              <li className="navbar-item">
                <Link to={"/"+this.state.id+"/order"} className="nav-link">Order Product</Link> 
              </li>
              <li className="navbar-item">
                <Link to={"/"+this.state.id+"/vieworder"} className="nav-link">View Orders</Link>
              </li>
            </ul>
          </div>
        </nav>
        Serach <t/> 
        <input onChange={this.onChangeSearch} />
        <div style={{color:"red"}} className="error">{this.state.searcherror}</div>
        <br/>
        <br/>
        Sort by <t/>
        <button  onClick={this.sortbyprice(1)}>
        Price Ascending
        </button>
         <button onClick={this.sortbyprice(-1)}>
        Sort by Price Descending
        </button>
         <button onClick={this.sortbyquantity(1)}>
        Quantity Descending
        </button>
        <button onClick={this.sortbyquantity(-1)}>
        Quantity Ascending
        </button>
         <button onClick={this.sortbyquantity(-1)}>
        Rating Ascending
        </button>
        <button onClick={this.sortbyquantity(-1)}>
        Rating Descending
        </button>
        <br/>
        <br/>

                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>productName</th>
                            <th>bundlePrice</th>
                            <th>leftQuantity</th>
                        </tr>
                    </thead>
                    <tbody>
                    { 
                        this.state.users.map((currentUser, i) => {
                            return (
                                <tr>
                                    <td id={i}>{currentUser.productName}</td>
                                    <td id={i+1}>{currentUser.bundlePrice}</td>
                                    <td id={i+2}>{currentUser.leftQuantity}</td>
                                    <td>
                                    <input onChange={this.onChangeorder({currentUser})} />
                                    <button id="adjmsfilk" onClick={this.orderfun({currentUser})} >
                                      Order
                                    </button>
                                  </td>
                                </tr>
                            )
                        })
                    }
                    </tbody>
                </table>
            </div>
        )
    }
}
