import React, {Component} from 'react';
import "bootstrap/dist/css/bootstrap.min.css"
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

import axios from 'axios';

export default class VendorView extends Component {

    constructor(props) {
        super(props);
        this.state = {
                        users :[],
                        id: this.props.match.params.id,
                        value : '',
                        orderarr : {},
                        freevar : 0
                     }
        this.fun = this.fun.bind(this);
        this.onChangeorder = this.onChangeorder.bind(this);


    }

    onChangeorder = arg => event =>
    {
        orderarr[arg["currentUser"]["_id"]] = event.target.value;  
        this.setState({ });
        console.log(event.target.value);
        axios.get('http://localhost:4000/product/')
             .then(response => {
                 this.setState({users: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
    }

    fun = arg => event =>
    {
         console.log(arg["currentUser"]["_id"]);
        axios.get('http://localhost:4000/product/')
             .then(response => {
                 this.setState({users: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
    }

    componentDidMount() {
        axios.get('http://localhost:4000/product/')
             .then(response => {
                 this.setState({users: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
    }

    render() {
        return (
                  <div className="container">
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <Link to="/" className="navbar-brand"> App </Link>

          <div className="collapse navbar-collapse">
            <ul className="navbar-nav mr-auto">
              <li className="navbar-item">
                <Link to={"/"+this.state.id+"/order"} className="nav-link">Order Product</Link> 
              </li>
              <li className="navbar-item">
                <Link to={"/"+this.state.id+"/vieworder"} className="nav-link">View Orders</Link>
              </li>
            </ul>
          </div>
        </nav>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>productName</th>
                            <th>bundlePrice</th>
                            <th>leftQuantity</th>
                        </tr>
                    </thead>
                    <tbody>
                    { 
                        this.state.users.map((currentUser, i) => {
                            return (
                                <tr>
                                    <td id={i}>{currentUser._id}</td>
                                    <td id={i+1}>{currentUser.bundlePrice}</td>
                                    <td id={i+2}>{currentUser.leftQuantity}</td>
                                    <td>
                                    <input onChange={this.onChangeorder({currentUser})} value={this.state.password}/>
                                    <button id="adjmsfilk" onClick={this.fun({currentUser})} >
                                      Order
                                    </button>
                                  </td>
                                </tr>
                            )
                        })
                    }
                    </tbody>
                </table>
            </div>
        )
    }
}
