import React, {Component} from 'react';
import "bootstrap/dist/css/bootstrap.min.css"
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

import axios from 'axios';

export default class VendorViewAdd extends Component {

    constructor(props) {
        super(props);
        this.state = {
                        users :[],
                        id: this.props.match.params.id,
                        productname : '',
                        productnameerror: '',
                        bundlepriceerror: '',
                        bundlequantityerror : '',
                        bundleprice : '',
                        bundlequantity: ''
                     }
        this.onChangeProductName = this.onChangeProductName.bind(this);
        this.onChangeBundleQuantity = this.onChangeBundleQuantity.bind(this);
        this.onChangeBundlePrice = this.onChangeBundlePrice.bind(this);


    }


    componentDidMount() {
        axios.get('http://localhost:4000/product/'+this.state.id)
             .then(response => {
                 this.setState({users: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
    }
     onChangeProductName(event) {
        this.setState({ productname: event.target.value });
     }
     onChangeBundlePrice(event) {
        this.setState({ bundleprice: event.target.value });
     }
     onChangeBundleQuantity(event) {
        this.setState({ bundlequantity: event.target.value });
     }
     onSubmit(e) {
        e.preventDefault();

        const newProduct = {
            productname: this.state.productname,
            bundlequantity: this.state.bundlequantity,
            bundleprice: this.state.bundleprice
        }
            axios.post('http://localhost:4000/product', newProduct)
            .then(res => console.log(res.data));

        this.setState({
            username: '',
            password: ''
        });
    }
    render() {
        return (
                  <div className="container">
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <Link to="/" className="navbar-brand"> App </Link>

          <div className="collapse navbar-collapse">
            <ul className="navbar-nav mr-auto">
              <li className="navbar-item">
                <Link to={"/"+this.state.id+"/addproduct"} className="nav-link">Add Product</Link> 
              </li>
              <li className="navbar-item">
                <Link to={"/"+this.state.id+"/listproduct"} className="nav-link">View Listing</Link>
              </li>
            </ul>
          </div>
        </nav>
                 <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Product Name: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.productname}
                               onChange={this.onChangeProductName}
                               />
                                <div style={{color:"red"}} className="error">{this.state.producterror}</div>
                    </div>
                    <div className="form-group">
                        <label>Bundle Quantity: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.bundlequantity}
                               onChange={this.onChangeBundleQuantity}
                               />
                                <div style={{color:"red"}} className="error">{this.state.bundlequantityerror}</div>
                    </div>
                    <div className="form-group">
                        <label>Bundle Price: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.bundleprice}
                               onChange={this.onChangeBundlePrice}
                               />
                                <div style={{color:"red"}} className="error">{this.state.bundlepriceerror}</div>
                    </div>

                    <div className="form-group">
                        <input type="submit" value="Create User" className="btn btn-primary"/>
                    </div>
                </form>
            </div>
        )
    }
}
