import React, {Component} from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import { Redirect   } from "react-router-dom";

import "bootstrap/dist/css/bootstrap.min.css"
import axios from 'axios';

export default class Login extends Component {

    constructor(props) {
        super(props);

        this.state = {
            username: '',
            password: '',
            type: '',
            useerror:'',
            passerror:'',
            auth : 0
        }

        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        this.onChangeType = this.onChangeType.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }

 onChangeType(event) {
        console.log(event.target.value);
        this.setState({ type: event.target.value });
    }

     onChangeUsername(event) {
        this.setState({ username: event.target.value });
          this.setState({ useerror: "Username does not exists" });
        axios.get('http://localhost:4000/customer/'+event.target.value)
             .then(response => {
                // console.log(response.data[0]["password"]);
                if (response.data.length===1) 
               {
                    this.setState({ useerror: "" });
                }
             })
        axios.get('http://localhost:4000/vendor/'+event.target.value)
             .then(response => {
                // console.log(response.data[0]["password"]);
                if (response.data.length===1) 
               {
                    this.setState({ useerror: "" });
                }
             })

    }
    onChangePassword(event) {
        this.setState({ password: event.target.value });
        console.log(event.target.value);
        this.setState({ passerror: "Incorrect Password" });
        axios.get('http://localhost:4000/customer/'+this.state.username)
             .then(response => {
                if (response.data.length===1&&response.data[0]["password"]===this.state.password) 
                {
                    this.setState({ passerror: "" });

                }
             })
        axios.get('http://localhost:4000/vendor/'+this.state.username)
             .then(response => {
                if (response.data.length===1&&response.data[0]["password"]===this.state.password) 
                {
                    this.setState({ passerror: "" });

                }
             })
    }

    onSubmit(e) {
        e.preventDefault();
        if (this.state.type==="Vendor")
            {
         axios.get('http://localhost:4000/'+this.state.username)
             .then(response => {
                if (response.data[0]["password"]===this.state.password) 
                {
                    this.setState({ passerror: "" });
                    this.setState({ auth: 1 });

                }
                else
                {

                    this.setState({ passerror: "Incorrect Password" });
                    this.setState({ auth: 0 });
                }
             })
             .catch(function(error) {
                 // console.log(error);
             })
        }
        if (this.state.type==="Customer")
            {
         axios.get('http://localhost:4000/customer/'+this.state.username)
             .then(response => {
                if (response.data[0]["password"]===this.state.password) 
                {
                    this.setState({ passerror: "" });
                    this.setState({ auth: 1 });

                }
                else
                {

                    this.setState({ passerror: "Incorrect Password" });
                    this.setState({ auth: 0 });
                }
             })
             .catch(function(error) {
                 // console.log(error);
             })
        }
        const newUser = {
            username: this.state.username,
            email: this.state.email
        }



    }

    render() {
        if (this.state.auth===1)
         {
            if (this.state.type==="Vendor")
            {
                console.log(127638)
                return( <Redirect to={"/vendorview/"+this.state.username}/>)
            }
            else if(this.state.type==="Customer")
            {   
                return( <Redirect to={"/customerview/"+this.state.username}/>)
            }
            else
            {   
                return( <Redirect to="/user"/>)
            }
         }
        return (
            <div className="container">
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <Link to="/" className="navbar-brand"> App </Link>

          <div className="collapse navbar-collapse">
            <ul className="navbar-nav mr-auto">
              <li className="navbar-item">
                <Link to="/user" className="nav-link">Login</Link> 
              </li>
              <li className="navbar-item">
                <Link to="/register" className="nav-link">Register</Link>
              </li>
            </ul>
          </div>
        </nav>
            <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Username: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.username}
                               onChange={this.onChangeUsername}
                               />
                                <div style={{color:"red"}} className="error">{this.state.useerror}</div>
                    </div>
                    <div className="form-group">
                        <label>Passwordd: </label>
                        <input type="password" 
                               className="form-control" 
                               value={this.state.password}
                               onChange={this.onChangePassword}
                               />  
                                <div style={{color:"red"}} className="error">{this.state.passerror}</div>
                    </div>
                    <div className="form-group">
                             <label>Vendor</label>
                            <input type="radio" name="type"  value="Vendor" onClick={this.onChangeType}/> 
                             <label>Customer</label>
                            <input type="radio" name="type" value="Customer" onClick={this.onChangeType}/>  
                    </div>
                    <div className="form-group">
                        <input type="submit" value="Login  " className="btn btn-primary"/>
                    </div>
                </form>
            </div>
        )
    }
}
