import React, {Component} from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css"
import axios from 'axios';

export default class CreateUser extends Component {

    constructor(props) {
        super(props);

        this.state = {
            username: '',
            password: '',
            type: '',
            useerror:'',
            passerror:''
        }
        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        this.onChangeType = this.onChangeType.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }

    onChangeType(event) {
        console.log(event.target.value);
        this.setState({ type: event.target.value });
    }

    onChangeUsername(event) {
        this.setState({ username: event.target.value });
        axios.get('http://localhost:4000/customer'+event.target.value)
             .then(response => {
                // console.log(response.data[0]["password"]);
                if (response.data.length) 
                {
                  this.setState({ useerror: "Username already exists" });
                }
                else
                {
                  this.setState({ useerror: "" });
                }
             })
             .catch(function(error) {
                 // console.log(error);
             })

    }
    onChangePassword(event) {
        this.setState({ password: event.target.value });
     }

    onSubmit(e) {
        e.preventDefault();

        const newUser = {
            username: this.state.username,
            password: this.state.password

        }
        if (this.state.type==="Vendor") 
        {
            axios.post('http://localhost:4000/vendoradd', newUser)
            .then(res => console.log(res.data));

        }
        else if(this.state.type==="Customer")
        {
            axios.post('http://localhost:4000/customeradd', newUser)
            .then(res => console.log(res.data));
        }     
        this.setState({
            username: '',
            password: ''
        });
    }

    render() {
        return (
            <div className="container">
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <Link to="/" className="navbar-brand"> App </Link>

          <div className="collapse navbar-collapse">
            <ul className="navbar-nav mr-auto">
              <li className="navbar-item">
                <Link to="/user" className="nav-link">Login</Link> 
              </li>
              <li className="navbar-item">
                <Link to="/register" className="nav-link">Register</Link>
              </li>
            </ul>
          </div>
        </nav>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Username: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.username}
                               onChange={this.onChangeUsername}
                               />
                                <div style={{color:"red"}} className="error">{this.state.useerror}</div>
                    </div>
                    <div className="form-group">
                        <label>Password: </label>
                        <input type="password" 
                               className="form-control" 
                               value={this.state.password}
                               onChange={this.onChangePassword}
                               />  
                                <div style={{color:"red"}} className="error">{this.state.passerror}</div>
                    </div>
                    <div className="form-group">
                             <label>Vendor</label>
                            <input type="radio" name="type"  value="Vendor" onClick={this.onChangeType}/> 
                             <label>Customer</label>
                            <input type="radio" name="type" value="Customer" onClick={this.onChangeType}/>  
                    </div>
                    <div className="form-group">
                        <input type="submit" value="Create User" className="btn btn-primary"/>
                    </div>
                </form>
            </div>
        )
    }
}
