import React, {Component} from 'react';
import axios from 'axios';

export default class Login extends Component {

    constructor(props) {
        super(props);

        this.state = {
            username: '',
            password: '',
            type: '',
            useerror:'',
            passerror:''
        }

        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }

     onChangeUsername(event) {
        this.setState({ username: event.target.value });
        axios.get('http://localhost:4000/'+event.target.value)
             .then(response => {
                // console.log(response.data[0]["password"]);
                if (response.data.length) 
                {
                  this.setState({ useerror: "" });
                }
                else
                {
                  this.setState({ useerror: "Username does not exists" });
                }
             })
             .catch(function(error) {
                 // console.log(error);
             })

    }
    onChangePassword(event) {
        this.setState({ password: event.target.value });
        console.log(event.target.value);
        axios.get('http://localhost:4000/'+this.state.username)
             .then(response => {
                if (response.data[0]["password"]===this.state.password) 
                {
                    this.setState({ passerror: "" });
                }
                else
                {
                    this.setState({ passerror: "Incorrect Password" });
                }
             })
             .catch(function(error) {
                 // console.log(error);
             })
    }

    onSubmit(e) {
        e.preventDefault();

        const newUser = {
            username: this.state.username,
            email: this.state.email
        }

        axios.post('http://localhost:4000/add', newUser)
             .then(res => console.log(res.data));

        this.setState({
            username: '',
            email: ''
        });
    }

    render() {
        return (

              <div className="container">
                <nav className="navbar navbar-expand-lg navbar-light bg-light">
                  <Link to="/" className="navbar-brand"> App </Link>

                  <div className="collapse navbar-collapse">
                    <ul className="navbar-nav mr-auto">
                      <li className="navbar-item">
                        <Link to="/user" className="nav-link">Login</Link> 
                      </li>
                      <li className="navbar-item">
                        <Link to="/register" className="nav-link">Register</Link>
                      </li>
                    </ul>
                  </div>
                </nav>
             <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Username: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.username}
                               onChange={this.onChangeUsername}
                               />
                                <div style={{color:"red"}} className="error">{this.state.useerror}</div>
                    </div>
                    <div className="form-group">
                        <label>Password: </label>
                        <input type="password" 
                               className="form-control" 
                               value={this.state.password}
                               onChange={this.onChangePassword}
                               />  
                                <div style={{color:"red"}} className="error">{this.state.passerror}</div>
                    </div>
                    <div className="form-group">
                        <input type="submit" value="Login  " className="btn btn-primary"/>
                    </div>
                </form>
            </div>
        )
    }
}
