import React, {Component} from 'react';
import "bootstrap/dist/css/bootstrap.min.css"
import {Button, Card} from 'react-bootstrap';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

import axios from 'axios';

export default class VendorView extends Component {

    constructor(props) {
        super(props);
        this.state = {
                        orders :[],
                        ProductName : [],
                        VendorName : [],
                        Quantity : [],
                        Status : [],
                        products :[],
                        id: this.props.match.params.id,
                        productid : ''
                     }

    }


    componentDidMount() {
        axios.get('http://localhost:4000/orders')
             .then(response => {
                 this.setState({orders: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })

        axios.get('http://localhost:4000/product')
             .then(response => {
                 this.setState({products: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })

    }

    render() {

        let page =[];

        page.push(
               <div className="container">
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <Link to="/" className="navbar-brand"> App </Link>

          <div className="collapse navbar-collapse">
            <ul className="navbar-nav mr-auto">
              <li className="navbar-item">
                <Link to={"/"+this.state.id+"/order"} className="nav-link">Order Product</Link> 
              </li>
              <li className="navbar-item">
                <Link to={"/"+this.state.id+"/vieworder"} className="nav-link">View Orders</Link>
              </li>
            </ul>
          </div>
        </nav>
            </div>
        )

        for (var i = 0; i <this.state.orders.length ; i++) {
          for (var j = 0; j < this.state.products.length; j++) {
              if (this.state.orders[i]["ProductId"]===this.state.products[j]["_id"]) 
              {
                this.state.ProductName.push(this.state.products[j]["productName"])
                console.log("adhjnsfk;");

          page.push(

              <div>
              <Card style={{ width: '20rem', left :'8%', upmargin:'10px'}}>
              <Card.Body>
                <Card.Title>Card Title</Card.Title>
                <Card.Text>
                ProductName : {this.state.products[j]["productName"]}
                <br/>
                VendorName :{this.state.products[j]["username"]}
                <br/>
                Quantity :{this.state.orders[i]["quantity"]}
                <br/>
                Status :{this.state.products[j]["status"]}
                <br/>
                </Card.Text>
                <Button variant="primary">Go somewhere</Button>
              </Card.Body>
            </Card>
              </div>

            )

              }
          }
        }



        return (
          <div>
          {page}
          </div>
        )





    }
}
