import React, {Component} from 'react';
import "bootstrap/dist/css/bootstrap.min.css"
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

import axios from 'axios';

export default class VendorView extends Component {

    constructor(props) {
        super(props);
        this.state = {
                        users :[],
                        id: this.props.match.params.id,
                        value : ''
                     }
        this.fun = this.fun.bind(this);


    }

    fun(event)
    {
        this.setState({ value: event.target.value });
      // console.log({event.target.value});
    }

    componentDidMount() {
        axios.get('http://localhost:4000/product/')
             .then(response => {
                 this.setState({users: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
    }

    render() {
        return (
                  <div className="container">
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <Link to="/" className="navbar-brand"> App </Link>

          <div className="collapse navbar-collapse">
            <ul className="navbar-nav mr-auto">
              <li className="navbar-item">
                <Link to={"/"+this.state.id+"/order"} className="nav-link">Order Product</Link> 
              </li>
              <li className="navbar-item">
                <Link to={"/"+this.state.id+"/vieworder"} className="nav-link">View Orders</Link>
              </li>
            </ul>
          </div>
        </nav>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>productName</th>
                            <th>bundlePrice</th>
                            <th>bundleQuantity</th>
                        </tr>
                    </thead>
                    <tbody>
                    { 
                        this.state.users.map((currentUser, i) => {
                            return (
                                <tr>
                                    <td>{currentUser.productName}</td>
                                    <td>{currentUser.bundlePrice}</td>
                                    <td>{currentUser.bundleQuantity}</td>
                                    <td>
                                    {" "}
                                    <button value={i} onClick={this.fun} >
                                      Order
                                    </button>
                                  </td>
                                </tr>
                            )
                        })
                    }
                    </tbody>
                </table>
            </div>
        )
    }
}
