import React, {Component} from 'react';
import "bootstrap/dist/css/bootstrap.min.css"
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

import axios from 'axios';

export default class VendorView extends Component {

    constructor(props) {
        super(props);
        this.state = {
                        users :[],
                        id: this.props.match.params.id

                     }

    }


    componentDidMount() {
        axios.get('http://localhost:4000/product'+this.state.id)
             .then(response => {
                 this.setState({users: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
    }

    render() {
        return (
                  <div className="container">
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <Link to="/" className="navbar-brand"> App </Link>

          <div className="collapse navbar-collapse">
            <ul className="navbar-nav mr-auto">
              <li className="navbar-item">
                <Link to={"/vendorview/"+this.id+"/addproduct"} className="nav-link">Add Product</Link> 
              </li>
              <li className="navbar-item">
                <Link to="/viewlisting" className="nav-link">View Listing</Link>
              </li>
            </ul>
          </div>
        </nav>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>productName</th>
                            <th>bundlePrice</th>
                            <th>bundleQuantity</th>
                        </tr>
                    </thead>
                    <tbody>
                    { 
                        this.state.users.map((currentUser, i) => {
                            return (
                                <tr>
                                    <td>{currentUser.productName}</td>
                                    <td>{currentUser.bundlePrice}</td>
                                    <td>{currentUser.bundleQuantity}</td>
                                </tr>
                            )
                        })
                    }
                    </tbody>
                </table>
            </div>
        )
    }
}
