import React, {Component} from 'react';
import "bootstrap/dist/css/bootstrap.min.css"
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

import axios from 'axios';

export default class VendorViewAdd extends Component {

    constructor(props) {
        super(props);
        this.state = {
                        users :[],
                        id: this.props.match.params.id,
                        productname : '',
                        bundleprice : '',
                        bundlequantity: ''
                     }

    }


    componentDidMount() {
        axios.get('http://localhost:4000/product/'+this.state.id)
             .then(response => {
                 this.setState({users: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
    }
     onChangeProductName(event) {
        this.setState({ password: event.target.value });
     }
    render() {
        return (
                  <div className="container">
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <Link to="/" className="navbar-brand"> App </Link>

          <div className="collapse navbar-collapse">
            <ul className="navbar-nav mr-auto">
              <li className="navbar-item">
                <Link to={"/"+this.state.id+"/addproduct"} className="nav-link">Add Product</Link> 
              </li>
              <li className="navbar-item">
                <Link to={"/"+this.state.id+"/listproduct"} className="nav-link">View Listing</Link>
              </li>
            </ul>
          </div>
        </nav>
                 <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Product Name: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.username}
                               onChange={this.onChangeProductName}
                               />
                                <div style={{color:"red"}} className="error">{this.state.useerror}</div>
                    </div>
                    <div className="form-group">
                        <label>Password: </label>
                        <input type="password" 
                               className="form-control" 
                               value={this.state.password}
                               onChange={this.onChangePassword}
                               />  
                                <div style={{color:"red"}} className="error">{this.state.passerror}</div>
                    </div>
                    <div className="form-group">
                             <label>Vendor</label>
                            <input type="radio" name="type"  value="Vendor" onClick={this.onChangeType}/> 
                             <label>Customer</label>
                            <input type="radio" name="type" value="Customer" onClick={this.onChangeType}/>  
                    </div>
                    <div className="form-group">
                        <input type="submit" value="Create User" className="btn btn-primary"/>
                    </div>
                </form>
            </div>
        )
    }
}
