import React, {Component} from 'react';
import "bootstrap/dist/css/bootstrap.min.css"
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

import axios from 'axios';

export default class VendorList extends Component {

    constructor(props) {
        super(props);
        this.state = {
                        users :[],
                        id: this.props.match.params.id

                     }
        this.orderCancel = this.orderCancel.bind(this);
        this.sortbyprice = this.sortbyprice.bind(this);
        this.sortbyquantity = this.sortbyprice.bind(this);

    }

    sortbyprice = arg=> event =>
    {
        axios.get('http://localhost:4000/product/sort/bundlePrice/'+this.state.id+"/waiting/"+arg)
             .then(response => {
                 this.setState({users: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })


    }

    sortbyquantity = arg=> event =>
    {
        axios.get('http://localhost:4000/product/sort/leftQuantity/'+this.state.id+"/waiting/"+arg)
             .then(response => {
                 this.setState({users: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })


    }

    orderCancel = arg => event =>
    {
         console.log(arg["currentUser"]["_id"]);

              const proddet = {
                  id :   arg["currentUser"]["_id"],
                  newleftQuantity : 0,
                  status : "Cancelled"

              }
             axios.post('http://localhost:4000/product/update', proddet)
            .then(res => console.log(res.data));


    }

    componentDidMount() {
        axios.get('http://localhost:4000/product/'+this.state.id+"/waiting")
             .then(response => {
                 this.setState({users: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
    }

    render() {
        return (
                  <div className="container">
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <Link to="/" className="navbar-brand"> App </Link>

          <div className="collapse navbar-collapse">
            <ul className="navbar-nav mr-auto">
              <li className="navbar-item">
                <Link to={"/"+this.state.id+"/addproduct"} className="nav-link">Add Product</Link> 
              </li>
              <li className="navbar-item">
                <Link to={"/"+this.state.id+"/listproduct"} className="nav-link">View Listing</Link>
              </li>
              <li className="navbar-item">
                <Link to={"/"+this.state.id+"/listdispatchproduct"} className="nav-link">Waiting for Dispatch</Link>
              </li>
              <li className="navbar-item">
                <Link to={"/"+this.state.id+"/listdispatchedproduct"} className="nav-link">Dispatched</Link>
              </li>
            </ul>
          </div>
        </nav>
        <br/>
        <button onClick={this.sortbyprice(1)}>
        Sort by Price Ascending
        </button>
         <button onClick={this.sortbyprice(-1)}>
        Sort by Price Descending
        </button>
         <button onClick={this.sortbyquantity(1)}>
        Sort by Quantity Descending
        </button>
        <button onClick={this.sortbyprice}>
        Sort by Quantity Ascending
        </button>
        <br/>
        <br/>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>productName</th>
                            <th>bundlePrice</th>
                            <th>leftQuantity</th>
                            <th>status</th>
                        </tr>
                    </thead>
                    <tbody>
                    { 
                        this.state.users.map((currentUser, i) => {
                            return (
                                <tr>
                                    <td>{currentUser.productName}</td>
                                    <td>{currentUser.bundlePrice}</td>
                                    <td >{currentUser.leftQuantity}</td>
                                    <td >{currentUser.status}</td>
                                    <td>
                                    <button id="adjmsfilk" onClick={this.orderCancel({currentUser})} >
                                      Cancel
                                    </button>
                                  </td>
                                </tr>
                            )
                        })
                    }
                    </tbody>
                </table>
            </div>
        )
    }
}
